package com.sd.shop.potatoes.integration.services;

import com.sd.shop.potatoes.entities.Image;
import com.sd.shop.potatoes.entities.Product;
import com.sd.shop.potatoes.entities.User;
import com.sd.shop.potatoes.repositories.CartRepository;
import com.sd.shop.potatoes.repositories.ImageRepository;
import com.sd.shop.potatoes.repositories.ProductRepository;
import com.sd.shop.potatoes.services.ShoppingService;
import com.sd.shop.potatoes.services.UserService;
import com.sd.shop.potatoes.repositories.UserRepository;

import org.junit.jupiter.api.AfterEach;
import org.junit.jupiter.api.BeforeEach;
import org.junit.jupiter.api.Test;
import org.junit.jupiter.params.ParameterizedTest;
import org.junit.jupiter.params.provider.CsvSource;
import org.junit.jupiter.params.provider.ValueSource;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.boot.test.context.SpringBootTest;
import org.springframework.context.annotation.Description;

import static org.junit.jupiter.api.Assertions.assertEquals;

@SpringBootTest
class ShoppingServiceTest {
    @Autowired
    ShoppingService shoppingService;

    @Autowired
    private UserService userService;

    @Autowired
    private UserRepository userRepository;

    @Autowired
    private ProductRepository productRepository;

    @Autowired
    private ImageRepository imageRepository;

    @Autowired
    private CartRepository cartRepository;

    @BeforeEach
    void setUp() {
        Product savedProduct = productRepository.save(new Product(
                "Yukon Gold",
                "Yukon Gold potatoes have finely flaked yellowish-white skin with light yellow flesh. They're bright, vegetal and slightly sweet, with a smooth, slightly waxy texture and moist flesh. They're best for boiling, baking and making French fries. They'll also stand up well to grilling, pan frying and roasting.",
                5,
                2.75,
                Product.Currency.EUR
        ));

        imageRepository.save(new Image(
                "image 1",
                "data:image/png;base64, ",
                savedProduct.getId()
        ));

        imageRepository.save(new Image(
                "image 2",
                "data:image/png;base64, ",
                savedProduct.getId()
        ));

        productRepository.save(new Product(
                "Idaho Russet",
                "Idaho Russet potatoes are russet-skinned with white flesh. They're what we typically imagine when we think of potatoes. They have a neutral potato flavor, a fluffy, creamy and soft texture, and are best for baking, mashing and making French fries. They're also very absorbent, so are great paired with butter and cream -- think mashed potatoes. Just don't try using Idaho Russets for potato salads, gratins or any dish that requires the potatoes to hold their shape.",
                10,
                3.75,
                Product.Currency.EUR
        ));

        userService.save(new User(
                "Jonas",
                "Jonas",
                "Jonauskas",
                "password",
                "password",
                User.Role.BUYER
        ));

        userService.save(new User(
                "Jonas 2",
                "Jonas 2",
                "Jonauskas",
                "password",
                "password",
                User.Role.BUYER
        ));
    }

    @AfterEach
    void tearDown() {
        cartRepository.deleteAll();
        productRepository.deleteAll();
        imageRepository.deleteAll();
        userRepository.deleteAll();
    }

    @Test
    @Description("Must get quantity equal to 1 for given user id.")
    void mustGetOneProductForUserId() {
        productRepository.findById(1L).ifPresent(p -> {
            shoppingService.addOrRemoveProductToUserCart(1L, true, p);
        });

        int quantity = shoppingService.getProductQuantityForUserId(1L);

        assertEquals(1 , quantity);
    }

    @ParameterizedTest
    @CsvSource({ "1, 1", "3, 3" })
    @Description("Must get quantity equal to 1 for given user ids.")
    void mustGetOneProductForUserIds(Long userId, Long productId) {
        productRepository.findById(productId).ifPresent(p -> {
            shoppingService.addOrRemoveProductToUserCart(userId, true, p);
        });

        int quantity = shoppingService.getProductQuantityForUserId(userId);

        assertEquals(1 , quantity);
    }

    @Test
    void shouldRemoveProduct() {
        productRepository.findById(1L).ifPresent(p -> {
            shoppingService.addOrRemoveProductToUserCart(1L, true, p);
        });

        productRepository.findById(1L).ifPresent(p -> {
            shoppingService.addOrRemoveProductToUserCart(1L, false, p);
        });

        assertEquals(0, shoppingService.getProductQuantityForUserId(1L));
    }
}